import styled from "styled-components";
import { Button } from "../../../../components/button/Button";

type ProjectType = {
	bgcImage: string;
	projectTheme: string;
	title: string;
	text: string;
	btnLink: string;
	btnTitle: string;
};

export function Project(props: ProjectType) {
	return (
		<StyledProject bgcImage={props.bgcImage}>
			<ProjectTheme>{props.projectTheme}</ProjectTheme>
			<Title>{props.title}</Title>

			<Textproject>
				<Text>{props.text}</Text>
				<Button btnTitle={props.btnTitle} btnLink={props.btnLink} />
			</Textproject>
		</StyledProject>
	);
}

type StyledProjectType = {
	bgcImage: string;
};

const StyledProject = styled.div<StyledProjectType>`
	background-image: url(${(props) => props.bgcImage});
	background-position: 100%;
	background-size: cover;
	max-width: 1040px;
	color: white;
	& {
		margin-bottom: 70px;
	}
	display: flex;
`;

const HeaderProject = styled.div``;

const Title = styled.h3``;

const Textproject = styled.div``;

const Text = styled.p`
	display: flex;
	width: 433px;
	flex-direction: column;
	align-items: flex-start;
	gap: 24px;
`;
