import styled from "styled-components";
import { Button } from "../../../../components/button/Button";

type ProjectType = {
	bgcImage: string;
	projectTheme: string;
	title: string;
	text: string;
	btnLink: string;
	btnTitle: string;
};

export function Project(props: ProjectType) {
	return (
		<StyledProject bgcImage={props.bgcImage}>
			<HeaderProject>
				<span>{props.projectTheme}</span>
				<Title>{props.title}</Title>
			</HeaderProject>
			<Textproject>
				<Text>{props.text}</Text>
				<Button btnTitle={props.btnTitle} btnLink={props.btnLink} />
			</Textproject>
		</StyledProject>
	);
}

type StyledProjectType = {
	bgcImage: string;
};

const StyledProject = styled.div<StyledProjectType>`
	background-image: url(${(props) => props.bgcImage});
	max-width: 1040px;

	background-position: 100% 100%;
	background-size: cover;
	/* max-width: 500;
	max-height: 400; */
	color: white;
	/* display: flex;
	gap: 50px; */
	&{
		margin-bottom: 70px;
	}
`;

const Title = styled.h3``;

const Text = styled.p`
`;
